import React, { useState, useEffect } from 'react'
import { Helmet } from 'react-helmet'
import { Link } from 'react-router-dom'
import './writer-portal.css'
import Footer3 from '../components/footer3'

const WriterPortal = () => {
  const [title, setTitle] = useState('')
  const [tags, setTags] = useState('')
  const [content, setContent] = useState('')
  const [quill1, setQuill1] = useState(null)
  const [quill2, setQuill2] = useState(null)

  useEffect(() => {
    const script = document.createElement('script')
    script.src = 'https://cdn.jsdelivr.net/npm/quill@2.0.3/dist/quill.js'
    script.onload = () => {
      const hljsScript = document.createElement('script')
      hljsScript.src = 'https://cdnjs.cloudflare.com/ajax/libs/highlight.js/11.9.0/highlight.min.js'
      hljsScript.onload = () => {
        const Parchment = window.Quill.import('parchment')
        const Delta = window.Quill.import('delta')
        const FontAttributor = window.Quill.import('attributors/class/font')
        const Bold = window.Quill.import('formats/bold')
        const Italic = window.Quill.import('formats/italic')
        const Underline = window.Quill.import('formats/underline')

        // Customize font whitelist
        FontAttributor.whitelist = ['sofia', 'slabo', 'roboto', 'inconsolata', 'ubuntu']
        window.Quill.register(FontAttributor, true)

        // Customize bold format to use <b> tag
        Bold.tagName = 'B'
        window.Quill.register(Bold, true)

        // Custom module to log content changes
        class LogContentModule {
          constructor(quill, options) {
            quill.on('text-change', () => {
              console.log('Editor content changed:', quill.root.innerHTML)
            })
          }
        }
        window.Quill.register('modules/logContent', LogContentModule)

        class PlainClipboard extends window.Quill.import('modules/clipboard') {
          convert(html = null) {
            if (typeof html === 'string') {
              this.container.innerHTML = html
            }
            let text = this.container.innerText
            this.container.innerHTML = ''
            return new Delta().insert(text)
          }
        }

        window.Quill.register('modules/clipboard', PlainClipboard, true)

        // Create custom registries
        const registry1 = new Parchment.Registry()
        const registry2 = new Parchment.Registry()

        // Register formats for the first registry
        registry1.register(Bold)
        registry1.register(Italic)
        registry1.register(FontAttributor)

        // Register formats for the second registry
        registry2.register(Bold)
        registry2.register(Italic)
        registry2.register(Underline)
        registry2.register(FontAttributor)

        const toolbarOptions1 = [
          ['bold', 'italic'], // Only bold and italic
          [{ 'font': FontAttributor.whitelist }]
        ]

        const toolbarOptions2 = [
          ['bold', 'italic', 'underline'], // Bold, italic, and underline
          [{ 'font': FontAttributor.whitelist }]
        ]

        const quillInstance1 = new window.Quill('#editor1', {
          theme: 'snow',
          modules: {
            toolbar: toolbarOptions1,
            syntax: { hljs: window.hljs },
            clipboard: true,
            logContent: true
          },
          registry: registry1,
          placeholder: 'Compose an epic...',
          formats: ['bold', 'italic', 'font']
        })

        const quillInstance2 = new window.Quill('#editor2', {
          theme: 'snow',
          modules: {
            toolbar: toolbarOptions2,
            syntax: { hljs: window.hljs },
            clipboard: true,
            logContent: true
          },
          registry: registry2,
          placeholder: 'Compose another epic...',
          formats: ['bold', 'italic', 'underline', 'font']
        })

        setQuill1(quillInstance1)
        setQuill2(quillInstance2)
      }
      document.body.appendChild(hljsScript)
    }
    document.body.appendChild(script)
  }, [])

  const handleInsertText = (quill) => {
    if (quill) {
      quill.insertText(0, 'Hello, Quill!', 'bold', true)
    }
  }

  const handleGetText = (quill) => {
    if (quill) {
      const text = quill.getText(0, 10)
      console.log('First 10 characters:', text)
    }
  }

  const handleApplyDelta = (quill) => {
    if (quill) {
      const Delta = window.Quill.import('delta')
      const delta = new Delta()
        .retain(7) // Keep 'Hello, '
        .insert('World', { color: '#ff0000' }) // Insert 'World' with red color
        .delete(6) // Delete 'Quill!'
      quill.updateContents(delta)
    }
  }

  const handlePasteHTML = (quill) => {
    if (quill) {
      quill.clipboard.dangerouslyPasteHTML('<p><b>Bold Text</b> and <i>Italic Text</i></p>')
    }
  }

  return (
    <div className="writer-portal-container">
      <Helmet>
        <title>Writer Portal - AI Research Hubs</title>
        <meta property="og:title" content="Writer Portal - AI Research Hubs" />
      </Helmet>
      <header className="writer-portal-header">
        <nav className="writer-portal-navbar">
          <Link to="/writer-portal" className="writer-portal-navlink">Writer Portal</Link>
          <Link to="/payment-details" className="writer-portal-navlink">Payment Details</Link>
          <Link to="/published" className="writer-portal-navlink">Published</Link>
        </nav>
        <h1>Writer Portal</h1>
        <p>Explore opportunities to contribute to AI Research Hubs.</p>
      </header>
      <section className="writer-portal-content">
        <h2>Draft Your Article</h2>
        <form className="writer-portal-form">
          <input
            type="text"
            placeholder="Article Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Article Tags (comma separated)"
            value={tags}
            onChange={(e) => setTags(e.target.value)}
            required
          />
        </form>
        <div id="editor1">
          <p>Start writing your article...</p>
        </div>
        <button onClick={() => handleInsertText(quill1)}>Insert Text</button>
        <button onClick={() => handleGetText(quill1)}>Get Text</button>
        <button onClick={() => handleApplyDelta(quill1)}>Apply Delta</button>
        <button onClick={() => handlePasteHTML(quill1)}>Paste HTML</button>
        <div id="editor2">
          <p>Start writing your article...</p>
        </div>
        <button onClick={() => handleInsertText(quill2)}>Insert Text</button>
        <button onClick={() => handleGetText(quill2)}>Get Text</button>
        <button onClick={() => handleApplyDelta(quill2)}>Apply Delta</button>
        <button onClick={() => handlePasteHTML(quill2)}>Paste HTML</button>
      </section>
      <Footer3 />
    </div>
  )
}

export default WriterPortal
